import React, { useState } from "react";

const Toggle = ({ title }) => {
const {showTitle, setShowTitle } = useState(true);

    return <div>
        <h1>Toggle Component</h1>
        { showTitle ? <h1>{ title }</h1> : '' }
        <button onClick={() => setShowTitle(!showTitle)}>
            Toggle Title
        </button>
    </div>
}

export default Toggle;
